(window.webpackJsonp=window.webpackJsonp||[]).push([[348],{620:function(v,_,t){"use strict";t.r(_);var a=t(14),e=Object(a.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("https://www.voltairenet.org/article198970.html")]),v._v(" "),_("h1",{attrs:{id:"что-ищет-израиль-в-далекои-аргентине"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#что-ищет-израиль-в-далекои-аргентине"}},[v._v("#")]),v._v(" Что ищет Израиль в далёкой Аргентине?")]),v._v(" "),_("p",[v._v("Аргентинские власти озабочены тем, что некий британский миллиардер массово скупает земли в Патагонии, а десятки тысяч израильских солдат проводят на этих землях свои «отпуска».")]),v._v(" "),_("p",[_("img",{attrs:{src:"https://www.voltairenet.org/local/cache-vignettes/L400xH300/198963-3-cce7f.jpg?1651891974",alt:""}})]),v._v(" "),_("p",[_("strong",[v._v("Владелец 175 компаний, среди которых ресторанные сети, футбольный клуб «Тоттенхем» и самопровозглашённая «Армия Йиды», и осторожный в делах миллиардер Джо Льюис спекулирует на валютном рынке вместе со своим приятелем и партнёром Джорджем Соросом.")])]),v._v(" "),_("p",[v._v("ВXIX веке правительство Великобритании намеревалось создать новое государство Израиль, но колебалось в выборе территории для его размещения: в сегодняшней Уганде, Аргентине или в Палестине. Аргентина в то время находилась под властью Соединённого Королевства и по инициативе французского барона Мориса Гирса стала прибежищем для евреев из Центральной Европы, сбежавших оттуда из-за погромов.")]),v._v(" "),_("p",[v._v("В XX веке после военного переворота против генерала Хуана Перона Главнокомандующим вооружённых сил становится демократически избранный президент страны и явный антисемит. В выпущенной им брошюре он обвиняет вновь созданное государство Израиль в подготовке вторжения в Патагонию, согласно так называемому «плану Андиния».")]),v._v(" "),_("p",[v._v("Если крайне-правые в Аргентине в 70-х годах и преувеличивали факты, и никакого плана вторжения в Патагонию не существовало, то план внедрения на эту территорию, по-видимому, всё-таки был.")]),v._v(" "),_("p",[v._v("Всё изменилось, когда в 1982 г. началась война за Мальвины. В то время военная хунта пыталась возвратить Мальвинские острова, Южную Георгию и Южные Сандвичевы острова, захваченные, по её мнению, британцами полтора века назад. ООН признала требования Аргентины законными, тогда как Совет безопасности осудил использование силы для возврата территорий. Но победителя ждала большая награда, так как территориальные воды этих архипелагов - это ключ к богатствам антарктического континента.")]),v._v(" "),_("p",[v._v("По окончании войны, в которой погибло более тысячи человек (британские официальные данные очень занижены), Лондон предлагает Буэнос-Айресу чрезмерно жестокий мирный договор. Вооружённые силы Аргентины сведены к минимуму. Но самое главное, контроль воздушного пространства южной части страны и всей Антарктики передан ВВС Королевства, и они должны информировать Соединённое Королевство о всех своих операциях.")]),v._v(" "),_("p",[v._v("В 1992 и 1994 гг. были совершены особо кровавые и разрушительные теракты в посольстве Израиля и здании израильской ассоциации AMIA. Первый теракт произошёл в Америке в то время, когда начальников смен израильской разведслужбы уже не было в здании посольства. А второй - при проведении совместных с Египтом исследовательских работ по созданию баллистических ракет Condor. В это же время происходит взрыв на территории самого крупного завода фирмы Condors, а в результате несчастных случаев гибнут сыновья президентов Карлоса Менема и Хафеза Ассада. Проведённые расследования выльются в серию разного рода манипуляций.")]),v._v(" "),_("p",[v._v("Указав сначала на Сирию, прокурор Альберто Нисман разворачивается против Ирана, назвав его заказчиком двух терактов, и против Хезболлы, являющейся якобы их исполнителем. Бывшая президент Кристина Киршнер обвиняется в заключении сделки об окончании преследований против Ирана взамен на поставку нефти по выгодной цене. Прокурора Нисмана найдут мертвым в своём доме, а президента Киршнер обвинят в предательстве национальных интересов. Однако на прошлой неделе оглушительное известие заставит изменить сложившееся мнение: ФБР обнародовала результаты анализа ДНК, которые свидетельствовали о том, что среди жертв теракта не было террориста, хотя одно из тел так и не было опознано. За 25 прошедших лет об этих терактах так ничего и не узнали.")]),v._v(" "),_("p",[v._v("В XXI веке, пользуясь преимуществами, которые им предоставил Договор по Мальвинским островам, Соединённое Королевство и Израиль приступают к реализации в Патагонии нового проекта.")]),v._v(" "),_("p",[v._v("Британский миллиардер Джо Льюис скупает на аге Аргентины и в соседней Чили огромные территории. Их площадь в несколько раз превосходит площадь Израиля. Они расположены на Огненной земле, крайнем Юге континента, в районе озёр Эскондидо и преграждают доступ к озёрам, несмотря на постановление суда.")]),v._v(" "),_("p",[v._v("Частный аэродром с взлётно-посадочной полосой длиной в 2 километра, построенный миллиардером, позволяет принимать как гражданские, так и военные транспортные самолёты.")]),v._v(" "),_("p",[v._v("После войны за Мальвины ВС Израиля организуют в Патагонии для своих солдат летние лагеря. Ежегодно на землях, скупленных Джо Льюисом, проводят свои двухнедельные «отпуска» от 8 до 10 тысяч израильских военных.")]),v._v(" "),_("p",[v._v("И если в 70-х годах аргентинская армия зафиксировала постройку казарм для размещения 25 000 военных, породив тем самым миф о «плане Андиния», то в настоящее время их построено столько, что в них можно разместить не одну сотню тысяч военных.")]),v._v(" "),_("p",[v._v("Эти земли являются частной собственностью, и Google Earth удаляет изображения этой территории, подобно тому, как он поступает в отношении снимков с изображением военных объектов, принадлежащих Атлантическому альянсу, поэтому оценить, в каком состоянии находятся указанные работы, практически невозможно.")]),v._v(" "),_("p",[v._v("Граничащая с Аргентиной Чили уступила свою военно-морскую базу, предназначенную для размещения подводных лодок, Израилю. Чтобы выживать в условиях полярной зимы, там были прорыты туннели.")]),v._v(" "),_("p",[v._v("Индейцы мапуче, проживающие в аргентинской и в чилийской Патагонии, были удивлены возобновлением в Лондоне деятельности таинственной организации Resistencia Ancestral Mapuche (СДМ - Сопротивление древних мапуче), требовавшей для них независимость. Сначала считалось, что к восстановлению этой организации причастны аргентинские спецслужбы, а теперь СДМ рассматривается левыми как легальное сепаратистское движение индейских мапуче, хотя лидеры движения финансируются Джорджем Соросом.")]),v._v(" "),_("p",[v._v("15 ноября 2017 г. ВМС Аргентины потеряли контакт с подводной лодкой "),_("em",[v._v("San Juan")]),v._v(", а впоследствии было заявлено, что она потерпела аварию. Это одна из двух дизельных подводных лодок типа TR 1700, являющихся жемчужинами немногочисленных вооружённых сил Аргентины. Подготовительная комиссия Организации договора о всеобъемлющем запрещении ядерных испытаний заявила о регистрации в Атлантике необычного акустического сигнала вблизи района, из которого подлодка "),_("em",[v._v("San Juan")]),v._v(" подала последний сигнал. Правительство в конечном итоге признало, что подлодка осуществляла некую «секретную миссию», о которой Лондон был информирован. США сразу приступили к поисковым работам, а ВМФ России направили дрон, способный исследовать морское дно до глубины 6000 метров, но лодку так и не нашли. Скорее всего, на "),_("em",[v._v("San Juan")]),v._v(" произошёл взрыв. Аргентинская пресса убеждена в том, что лодка либо натолкнулась на мину, либо была уничтожена вражеской торпедой.")]),v._v(" "),_("p",[v._v("На сегодняшний день нельзя с уверенностью утверждать, приводит Израиль в действие программу освоения Антарктики или он создаёт себе базу для отступления в случае поражения в Палестине.")])])}),[],!1,null,null,null);_.default=e.exports}}]);