(window.webpackJsonp=window.webpackJsonp||[]).push([[361],{629:function(_,v,p){"use strict";p.r(v);var e=p(14),t=Object(e.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("https://lechaim.ru/academy/peterburg/")]),_._v(" "),v("h2",{attrs:{id:"окно-в-россию-евреи-в-петербурге"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#окно-в-россию-евреи-в-петербурге"}},[_._v("#")]),_._v(" Окно в Россию: евреи в Петербурге")]),_._v(" "),v("p",[v("a",{attrs:{href:"https://lechaim.ru/writer/d0-b0-d0-bb-d0-b5-d0-ba-d1-81-d0-b0-d0-bd-d0-b4-d1-80-d0-bb-d0-be-d0-ba-d1-88-d0-b8-d0-bd/",target:"_blank",rel:"noopener noreferrer"}},[_._v("Александр Локшин"),v("OutboundLink")],1),_._v(" 7 июня 2018")]),_._v(" "),v("p",[v("strong",[_._v("Живший в середине XVIII века раввин Арье-Лейб Эпштейн уверял, будто само Провидение распорядилось, чтобы евреи не селились в Петербурге. Причина была серьезная: в пору белых ночей солнце там никогда не заходит, и, стало быть, в эти месяцы нельзя определить время утренней и вечерней молитв. Город на Неве воспринимался как особенный, ни с каким иным не сравнимый. В еврейском национальном сознании, в еврейском фольклоре многие города и даже целые области Российской империи вообще обретали свой особый образ. Им давались четкие характеристики. Вильно, к примеру, было «Иерушалаим де Лита» – «литовским Иерусалимом», известным центром раввинистической учености, центром издания книг на иврите. Одесса считалась южным прибежищем гедонизма и космополитизма. Немало людей было убеждено в том, что на семь верст вокруг нее горит адский огонь (на идише «зибн мейл арум Адес брент дер гехейном»). Город Хелм высмеивался в еврейском фольклоре за псевдомудрость его обитателей. А еврейское название Польши – Полин – хитро прочитывалось знатоками как «здесь осядем» (по-древнееврейски «по лин»). Предполагалась, таким образом, высшая санкция на поселение Б-жьего народа.")])]),_._v(" "),v("p",[v("strong",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/154/lokshin.files/image001.jpg",alt:""}})])]),_._v(" "),v("p",[v("em",[_._v("А. Н. Бенуа. Иллюстрации к «Медному всаднику» А. С. Пушкина. 1903 год")])]),_._v(" "),v("p",[_._v("Однако в Петербурге – столице огромной империи – еврейское прошлое отсутствовало. Это остро ощутили тысячи евреев, которые с начала 1860-х годов по новым законам, принятым царем Александром II, cтали в ней селиться. Они со страхом поняли, что в этом городе нет не только их национального прошлого, но нет фактически и настоящего.")]),_._v(" "),v("p",[_._v("Еврейские «следы» в Петербурге, заметные с момента его основания (1703 год), сводились лишь к анекдотам. Были еще, правда, легенды, связанные с деятельностью временно проживавших в Питере влиятельных евреев или евреев тайных. В 1714 году Петр Великий привез с собой из Амстердама нового придворного шута Яна д’Акосту, по слухам – потомка португальских марранов. Другой марран, тоже вывезенный из Голландии, Антон Мануилович Дивьер, стал генерал-полицмейстером – первым руководителем столичной полиции. Михаила Сперанского, министра в правительстве Александра I, известного сторонника реформ, упрекали за потаенные встречи с состоятельным откупщиком Абрамом Перетцем. Примерно в то же время основатель хасидского движения Хабад раввин Шнеур-Залман бен Борух сидел в печально знаменитой Петропавловской крепости. Молодой немецкий раввин Макс Лилиенталь по приезде в российскую столицу в 1841 году узнал от одного крещеного иудея, что Николай I, взойдя на престол, предоставил евреям, временно поселившимся в Петербурге, выбор: крещение либо изгнание.")]),_._v(" "),v("p",[_._v("Словом, вряд ли можно предположить, что евреи, как крещеные, так и сохранившие веру отцов, могли чувствовать себя в Петербурге как дома. Для русского населения город тоже был не слишком привлекателен – главным образом благодаря своему неблагоприятному климату. Евреям же он представлялся чужим по другим причинам. Возможно, прежде всего по религиозным, о которых говорил раввин Арье-Лейб Эпштейн. Да и в дальнейшем у них сохранялось ощущение своей глубинной непричастности к этому месту, хотя и по более светским соображениям.")]),_._v(" "),v("p",[_._v("Мотив самого еврейского присутствия в столице как преступления, вне зависимости от реальных обстоятельств, появляется в ряде литературных произведений, начиная с «Исповеди преступника», написанной Гершоном Лившицем, русскоязычным сочинителем, во второй половине XIX столетия. Но наиболее запоминающийся образ петербургского еврея как невольного сокрушителя основ создан Яковом Штейнбергом, автором поэмы «Преступники», изданной в 1881 году. Местами, заимствуя слово в слово текст пушкинского «Медного всадника», Штейнберг подменяет бедного чиновника Евгения молодым евреем, который приехал в имперскую столицу, «обуреваемый жаждой знаний», но обнаружил, что за ним охотятся, как за дичью, ибо он не имеет соответствующего вида на жительство. Шаг за шагом следуя по маршруту Евгения, юноша в отчаянии обращается к статуе Петра Великого – основателя города, создателя Российской империи:")]),_._v(" "),v("p",[v("em",[_._v("И пало рабство вековое,")])]),_._v(" "),v("p",[v("em",[_._v("Освободился твой народ.")])]),_._v(" "),v("p",[v("em",[_._v("Свое признанье мировое")])]),_._v(" "),v("p",[v("em",[_._v("Как гражданин он сознает.")])]),_._v(" "),v("p",[v("em",[_._v("Но есть несчастное здесь племя,")])]),_._v(" "),v("p",[v("em",[_._v("России пасынок оно,")])]),_._v(" "),v("p",[v("em",[_._v("И лишь с него не снято бремя,")])]),_._v(" "),v("p",[v("em",[_._v("Ему свободы не дано!")])]),_._v(" "),v("p",[v("em",[_._v("Великий Петр, я пред тобою,")])]),_._v(" "),v("p",[v("em",[_._v("Я не преступник, я еврей,")])]),_._v(" "),v("p",[v("em",[_._v("И вот охотятся за мною;")])]),_._v(" "),v("p",[v("em",[_._v("Мне жить нельзя среди людей!")])]),_._v(" "),v("p",[v("em",[_._v("За что отечества, свободы")])]),_._v(" "),v("p",[v("em",[_._v("Сыны России лишены?..")])]),_._v(" "),v("p",[_._v("При всём том Петр I у Штейнберга разительно отличается от пушкинского Петра. Тронутый еврейскими слезами, каменный самодержец в финале поэмы как бы признает равноправие евреев с русскими. Здесь он уже просвещенный монарх и почти защитник угнетенных.")]),_._v(" "),v("p",[_._v("Летом 1858 года «Санкт-Петербургские ведомости» писали, что, несмотря на дачный сезон, на то, что постоянные жители города во множестве перебрались в окрестности столицы или укатили за море, Петербург не вымер. «Взамен опустевших домов гостиницы набиты приезжими… На улицах встречаются незнакомые лица, между которыми на всяком шагу резко выдается еврейский тип, к которому так не привык глаз петербургского жителя. В настоящую минуту – утвердительно можно сказать – на Вознесенском и Екатерингофском проспектах, на Большой Садовой, в Семеновском, Измайловском полках нет дома, где бы хотя одна комната не была занята каким-нибудь промышленным израильтянином».")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/154/lokshin.files/image002.jpg",alt:""}})]),_._v(" "),v("p",[v("em",[_._v("Пинкос петербургской общины. 1820 год")])]),_._v(" "),v("p",[_._v("История постоянного еврейского присутствия в имперской столице начинается всё-таки чуть позже, когда в результате политики реформ, проводившейся Александром II, стало возможным проживание определенных категорий евреев и за чертой оседлости.")]),_._v(" "),v("p",[_._v("В поразительно короткий срок петербургское еврейство создало абсолютно новый тип российского иудея: модернизированного, космополитичного, необычайно удачливого в своих предприятиях, добившегося очень многого в таких сферах деятельности, как юриспруденция, банковское дело и журналистика. Впрочем, этот новый тип, конечно, отнюдь не вытеснил отовсюду прежний тип еврея – забитого, отсталого, нередко низведенного на грань нищеты. Он просто стал сосуществовать с ним.")]),_._v(" "),v("p",[_._v("Небывалое впечатление, производимое «новыми евреями» Петербурга, отмечали многие. Не только их гонители. Когда британский филантроп сэр Мозес Монтефиоре вторично посетил Петербург в 1872 году, он был поражен разницей между тем, что увидел, и тем, что наблюдал прежде – во время предыдущего приезда в Россию в 1846 году. Прямо в столице он записал в своем дневнике:")]),_._v(" "),v("p",[_._v("«Я имел счастье видеть значительное количество наших единоверцев, отмеченных наградами императора различного достоинства. Ныне евреи здесь одеваются подобно обыкновенным джентльменам в Англии, Франции или Германии. Их школы хорошо посещаются, сами они стоят во главе каждого приличного предпринимательского дела и намерены способствовать процветанию своей общины и всей страны в целом… Вспоминая, в каком состоянии наши единоверцы в России находились двадцать шесть лет тому назад и принимая во внимание их теперешнее положение, становится понятно, сколь благодарные чувства они питают к императору, коему в огромной степени они обязаны своим процветанием».")]),_._v(" "),v("p",[_._v("Разумеется, численное превосходство среди евреев Петербурга оставалось за ремесленниками и мелкими торговцами. Однако не удивительно, что общественное внимание привлекали именно купцы, банкиры и юристы. Ни в какой другой еврейской общине России не было таких неслыханно богатых и процветающих людей. Петербург в короткий срок стал местом, которое избрала русско-еврейская плутократия; многие ее представители играли главную роль в нарождающихся сферах частного банковского дела, спекуляций на бирже и железнодорожного строительства. Полина Венгерова, еврейка, жительница столицы и автор знаменитых мемуаров «Воспоминания бабушки», вероятно, не слишком преувеличивала, когда писала об эпохе 60–70-х годов XIX века: «Никогда прежде евреи в Cанкт-Петербурге не вели такой благополучной жизни, поскольку столичные финансы частично находились в их руках».")]),_._v(" "),v("p",[_._v("В одной из еврейских газет Петербурга 1860-е годы были названы «лихорадочным десятилетием частного предпринимательства». По словам одного еврея, бывшего служащего банка, «в выходцах из черты оседлости происходила полная метаморфоза: откупщик превращался в банкира, подрядчик – в предпринимателя высокого полета, а их служащие – в столичных денди. Многие вороны напялили на себя павлиньи перья; выскочки из Балты и Конотопа через короткое время считали себя “аристократами” и смеялись над “провинциалами”».")]),_._v(" "),v("p",[_._v("Это язвительное свидетельство точно фиксирует меняющуюся роль еврейской финансовой элиты в период бурного развития капитализма в России. Еврейские финансисты, по крайней мере те, что жили в Петербурге, сколачивали свои состояния уже преимущественно в области государственного предпринимательства и поддерживали тесные связи с правительственными чиновниками.")]),_._v(" "),v("p",[_._v("Банкирский дом Гинцбурга – самый яркий тому пример. Крупные винные откупщики, поставщики продовольствия и обмундирования в русскую армию во время Крымской войны, Евзель Гинцбург и его сын Гораций учредили в 1859 году собственный банк в Петербурге; в дальнейшем они предоставляли государству огромные ссуды для многих правительственных нужд, включая нужды, связанные с Русско-турецкой войной 1877–1878 годов. Братья Поляковы (Самуил, Яков, Лазарь) финансировали строительство железных дорог и в результате были введены Александром II в потомственное дворянство, что было великой редкостью для евреев. В 1871 году Авраам Зак, служивший прежде у Гинцбургов, стал директором Петербургского учетного и ссудного банка, одного из крупнейших в империи. Владел же банком польско-еврейский магнат Леопольд Кроненберг. К этому списку могут быть добавлены многие…")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/154/lokshin.files/image003.jpg",alt:""}})]),_._v(" "),v("p",[v("em",[_._v("Петербургская хоральная синагога. И. Шапошников, Л. Бахман, А. Малов. 1891–1893 годы")])]),_._v(" "),v("p",[_._v("Потрясающий взлет большого числа евреев вызвал целую бурю негодования по поводу этих выскочек. Предсказать ее, впрочем, было нетрудно. В одном из малоизвестных своих произведений, в поэме «Балет» (1866), поэт Н. А. Некрасов, с трудом сводивший тогда в Петербурге концы с концами, писал:")]),_._v(" "),v("p",[v("em",[_._v("Стоит только на ложи взглянуть,")])]),_._v(" "),v("p",[v("em",[_._v("Где уселись банкирские жены, –")])]),_._v(" "),v("p",[v("em",[_._v("Сотня тысяч рублей, что ни грудь!..")])]),_._v(" "),v("p",[v("em",[_._v("В этих ложах – мужчины-евреи…")])]),_._v(" "),v("p",[v("em",[_._v("Доблесть, молодость, сила – пленяли")])]),_._v(" "),v("p",[v("em",[_._v("Сердце женское в древние дни.")])]),_._v(" "),v("p",[v("em",[_._v("Наши девы практичней, умнее,")])]),_._v(" "),v("p",[v("em",[_._v("Идеал их – телец золотой,")])]),_._v(" "),v("p",[v("em",[_._v("Воплощенный в седом иудее,")])]),_._v(" "),v("p",[v("em",[_._v("Потрясающем грязной рукой")])]),_._v(" "),v("p",[v("em",[_._v("Груды золота…")])]),_._v(" "),v("p",[_._v("Вновь народившаяся петербургская еврейская интеллигенция зачастую была весьма критически настроена по отношению к соплеменникам-богачам. Они раздражали ее хотя бы откровенным стремлением дистанцироваться от своих бедных единоверцев. Литератор Ури Ковнер, с гордостью носивший прозвище «еврейский Писарев», опубликовал роман «Вокруг золотого тельца» – своеобразный обвинительный акт против бывшего своего работодателя банкира А. И. Зака. Личный секретарь Зака литератор Григорий Богров оставил хлебное место, чтобы стать соредактором петербургского еврейского еженедельника «Рассвет» (1879–1883). Там, в соавторстве с племянником железнодорожного магната А. М. Варшавского, Марком Варшавским, он печатал редакционные статьи, осуждавшие роль, которую в общине играли купавшиеся в роскоши его единоверцы. Обобщенный образ петербургской еврейской финансовой элиты нарисовал известный русско-еврейский писатель Лев Леванда в романе «Исповедь дельца». Роман этот – история скоропалительного обогащения «нового» еврея. Он был частью еще не слишком богатой, но быстро растущей литературы, изображавшей жизнь иудеев среди русских. Ведущий еврейский писатель России того времени, Л. Леванда (1835–1888) публиковал прозу и публицистику почти во всей русско-еврейской периодике, а также в русских газетах «Санкт-Петербургские ведомости» и «Новое время». Cлужа в качестве «ученого еврея» при виленском губернаторе, он до самого начала волны погромов (1881–1882) был русофилом и страстным сторонником вхождения евреев в русское общество.")]),_._v(" "),v("p",[_._v("Герой-рассказчик «Исповеди» – некто Мордехай Шмальц, в эпоху Великих реформ оставляющий родное местечко. Мордехай полон решимости стать миллионером в Петербурге – в «центре, – по его словам, – самой разнообразной и кипучей деятельности обширнейшей в мире империи». Он был частицей потока молодых честолюбивых евреев (и евреек), по поводу которых раввин из его местечка как-то воскликнул: «Все бегут теперь в Петербург, за делом и без дела! Точно манна небесная про всех там припасена!» Попав в Петербург (не имея при этом необходимого вида на жительство), Шмальц оказывается втянутым в жестокое соперничество с другими искателями скорой наживы. Среди них и обрусевший польский аристократ Ближневич, и прибалтийский немецкий барон фон Вернер, и целый сонм евреев, возжелавших стать богачами. Шмальцу везет: он зарабатывает огромные деньги на биржевых сделках сомнительной законности, покупает дворец в фешенебельном столичном квартале и пытается вести образ жизни аристократа. Однако его богатство, а равно и богатство большинства иных персонажей романа Леванды испаряется так же быстро, как появилось. В финале повествования главный герой – абсолютно сломленный человек, жена его пребывает в состоянии глубокого нервного расстройства; дети же становятся им чужими (один из них даже совершает вероотступничество).")]),_._v(" "),v("p",[_._v("Сам Лев Леванда провел в российской столице относительно немного времени. Тем не менее петербургская газета «Русский еврей» похвалила его «Исповедь» за «высоко реалистическое» изображение еврейской жизни на ее страницах. Там действительно немало достоверного – и в житейском, и в психологическом смысле. Вот, к примеру, отец Шмальца, типичный местечковый еврей, предрекает разорение своему неразумному Мордехаю:")]),_._v(" "),v("p",[_._v("«Знаю, сын мой… знаю, что ты богат, очень богат… Но… ты не сердишься? Я питаю мало доверия к твоему богатству.")]),_._v(" "),v("p",[_._v("– Почему?")]),_._v(" "),v("p",[_._v("– Потому что оно приобретено было каким-то странным путем, а то, что приобретено не трудом, непрочно. Б-жье проклятие лежит на нем.")]),_._v(" "),v("p",[_._v("– Не понимаю, батюшка, что ты хочешь этим сказать.")]),_._v(" "),v("p",[_._v("– Я вот что хочу сказать: в мое время люди тоже богатели. От чего? От торговли, коммерции… Человек, бывало, торгует хлебом, лесом, мануфактурными товарами, разъезжает по ярмаркам, трудится. Б-г благословляет его труды, и он становится большим купцом… От чего разбогател ты, cын мой? Сколько я ни расспрашивал, никто не мог объяснить мне толком, чем ты торгуешь. Мне говорили о каких-то акциях, компаниях, бирже, я записывал эти мудреные слова и расспрашивал: что это – лес? Говорят: нет. Хлеб? Нет. Пенька? Тоже нет. Что же это такое? Из чего тут богатеть? Чем ты торгуешь?»")]),_._v(" "),v("p",[_._v("Если внезапный взлет петербургской еврейской элиты был загадкой для евреев черты, то неевреям он представлялся еще и чем-то и угрожающим. Многое в «Исповеди» Леванды в конечном счете уничтожало расхожее представление о незыблемой еврейской солидарности. Вновь и вновь описывает прозаик, как безжалостно конкурируют евреи и друг с другом, и со всеми остальными. «Они меня топили, – признается Шмальц, имея в виду своих финансовых конкурентов еврейского происхождения, – и я их топил при всяком удобном случае и с особенным удовольствием. Вот вам и вся наша хваленая солидарность». Если неевреи готовы были объяснять любой еврейский успех содействием кагала, то Шмальц настаивал на том, что еврейское торговое влияние – результат совсем иных вещей: «…Мы берем единственно и исключительно нашим темпераментом, нашим аскетизмом и нашей интенсивною и неутомимою деятельностью… В то время когда дельцы других народностей – прежде всего обыкновенные люди с человеческими страстями и похотями, эпикурейцы, увлекающиеся и отвлекаемые от дела кто музыкой, кто живописью, кто женщинами, лошадьми, собаками, охотой, спортом, картежной игрой, мы, дельцы-евреи, не отвлекаемся и не развлекаемся ничем, что не имеет прямого отношения к делу».")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/154/lokshin.files/image004.jpg",alt:""}})]),_._v(" "),v("p",[v("em",[v("strong",[_._v("Л. О. Леванда")])])]),_._v(" "),v("p",[_._v("В финале раннего романа Л. Леванды «Горячее время» (1875) главный герой, размышляя об особенностях еврейской ассимиляции, намечает ее пределы: «…Мы будем русскими, но для нас всегда останутся чуждыми русская лень, русская беззаботность, забубенность, бесстрастие и то, что называется широкой русской натурой».")]),_._v(" "),v("p",[_._v("Практически, если не брать в расчет деловые контакты, евреи, жившие в Петербурге, по большей части в чисто человеческие отношения с русскими не вступали. В отличие от русских, Петербург для них был не «окном в Европу», а скорее «окном в Россию». Правда, они в ту пору оставались по преимуществу зрителями у этого самого окна…")]),_._v(" "),v("p",[_._v("Несмотря на попытки Леванды развеять миф о еврейском единстве, неевреи полагали его реальностью, воспринимали как нечто само собой разумеющееся и нередко считали серьезной проблемой. Петербургский градоначальник П. А. Грессер и министр внутренних дел И. Н. Дурново были убеждены, что Гораций Гинцбург и другие крупные еврейские финансисты имели влияние на еврейских студентов, подозревавшихся в организации нигилистических волнений в Петербургском университете и других местах. Гинцбургу неоднократно угрожали высылкой из города в случае, если беспорядки не прекратятся. Поэт Иегуда-Лейб Гордон, хорошо знакомый с еврейством Питера и по житейскому опыту, и вследствие занимаемой должности (он был секретарем общинного правления), утверждал, что российское правительство «напугано призраком кагала».")]),_._v(" "),v("p",[_._v("Подобно «портовым евреям» Одессы, других приграничных общин, евреи Петербурга столкнулись с серьезным осложнением: свои общинные организации им предстояло создать буквально с нуля. Они ведь были далеки от вековых традиций, стоявших за старыми еврейскими центрами, такими, как Вильно, Люблин или Бердичев. Более того, в Петербурге заполнение этой лакуны, этой «tabula rasa» (как назвала город одна еврейская газета), началось на несколько десятилетий позже, чем в Одессе, и, таким образом, происходило на более поздней стадии внутреннего размежевания российского еврейства. Как и всё в столице, еврейская община находилась под усиленным наблюдением не только местной администрации, но и высших органов правительства, и петербургские евреи осознавали свою «представительскую» функцию перед окружавшим их миром. И евреи, и неевреи рассматривали столичную еврейскую общину как своеобразный показатель того, насколько успешной оказалась политика «выборочной» интеграции, и полагали, что при благоприятных обстоятельствах она может распространиться на русское еврейство в целом.")]),_._v(" "),v("p",[_._v("Еврейская элита, начавшая формироваться в Петербурге в 1860-х годах, была полна решимости приблизить бытие соплеменников к тому, что вели евреи в Берлине и в Париже. С этой целью в июле 1863 года группа примерно из ста известных столичных евреев решила создать специальное правление, предназначенное для ведения общинных дел. Из пятнадцати избранных делегатов две трети были купцами (возглавляли список Евзель и Гораций Гинцбурги), остальные работали врачами или просто только что окончили какое-нибудь высшее русское учебное заведение. Первым шагом правления стало назначение доктора Авраама Неймана (1809–1875), уроженца Германии, последователя реформистского иудаизма, главным (а из-за официальных ограничений и единственным) раввином петербургской общины. Предыдущие двадцать лет А. Нейман прожил в Риге, где был раввином рижской реформистской общины и преемником немецкого маскила Макса Лилиенталя. Едва умевший изъясняться на русском и идише, Нейман вел службы на немецком и древнееврейском языках. Службы эти устраивались для именитых купцов во временном молельном доме в самом центре Подьяческого района – места с наиболее «плотным» еврейским населением. Получивший, как то было принято в Германии, не только религиозное, но и университетское образование, Нейман, по словам одного наблюдателя, создал и возглавил первую «порядочную общину, по крайней мере, в интеллектуальном смысле».")]),_._v(" "),v("p",[_._v("Еврейство, приверженное более давним традициям, не приветствовало появление Неймана и его статус единственного официально утвержденного раввина. Даже у тех, кто «принял» религиозную реформу, оставалось известное чувство неудовлетворенности. Спустя шесть лет после назначения Неймана журналист (в будущем – крупный издатель) А. Е. Ландау публично критиковал раввина за отсутствие инициативы.")]),_._v(" "),v("p",[_._v("Для поколения молодых просвещенных евреев, желавших, чтобы их считали полноправными гражданами обновленной России, неумение Неймана говорить по-русски было неприятным напоминанием о давних культурных узах Гаскалы с Германией. Затянувшихся, с их точки зрения. В равной степени тревожило их то обстоятельство, что Нейман был избран самопровозглашенным правлением, а не представителями общины, как это обычно практиковалось в черте оседлости.")]),_._v(" "),v("p",[_._v("В результате состоятельные члены этого «самоизбранного» правления использовали общинные фонды (созданные на деньги бедняков), чтобы финансировать «реформированные» еврейские институты, чуждые религиозной практике их оппонентов. Проистекавшие отсюда противоречия временами вели даже к вспышкам насилия и рукоприкладству. Группа ремесленников, разъяренных высокой ценой на кошерное мясо, затеяла однажды в молитвенном доме Неймана драку с высокопоставленными членами общины. Глубинной причиной скандала стало, естественно, то, что один еврейский журналист назвал «эксплуатацией бедного еврейского люда компанией еврейских кулаков-мясников».")]),_._v(" "),v("p",[_._v("В общем, до еврейской солидарности явно было далеко. Ландау, в обычной своей манере, прокомментировал вопиющие различия между столичными евреями:")]),_._v(" "),v("p",[_._v("«Смешанность еврейского населения Петербурга поистине замечательна. Вы найдете между здешними евреями… людей всех классов общества, как по своему материальному положению, так и по степеням нравственного развития… От крайних консерваторов до ярких реформаторов, от неисправимых фанатиков и ханжей до людей истинно религиозных, но в то же время просвещенных и вполне веротерпимых».")]),_._v(" "),v("p",[_._v("До конца ХIХ века, пока значительная часть русских евреев-интеллектуалов не обратились к специфически еврейской форме народничества, откровенно-критические соображения о народных еврейских обычаях были неотъемлемой частью комментариев просветителей. К примеру, описывая свадьбу двух семей, которой он был свидетелем, А. Ландау высмеивал «крик, шум, cуматоху, беспорядок», «дикую» смесь языков и неправильное чтение молитв. Журналиста особенно вывело из себя то, что невесте запретили есть в течение целого дня, что гости повыдергали ей волосы, голову («на счастье!») вымазали сахаром и медом, а затем покрыли тяжелым париком. Ландау уверял своих читателей, что подобное «невежество и религиозное суеверие можно встретить теперь только в самых отдаленных захолустьях литовских местечек». Непомерно длинная брачная церемония, которую Нейман вел на немецком языке, только добавила нелепости этому событию в русской столице.")]),_._v(" "),v("p",[_._v("Одно из немногих дошедших до нас свидетельств о жизни петербургского еврейства, где открыто отдается предпочтение «прелестям Бердичева», – это воспоминания Полины Венгеровой. По прибытии в Петербург с мужем-купцом (дело происходило в 70-х годах XIX века), Венгерова была потрясена тем, что представители состоятельной еврейской элиты ограничили свою, так сказать, причастность к религиозной жизни только тремя днями в году: Йом Кипуром, первым днем Песаха и… Рождеством (последнее праздновалось якобы ради слуг). Некоторые отправлялись в молитвенный дом на субботнюю службу в экипаже, а не пешком, и в Йом Кипур ели в перерывах во время службы. Пасхальный сейдер был очень сильно сокращен и зачастую отмечался в память не об Исходе из Египта, а о прощании с чертой оседлости. Беседы за пасхальным столом быстро переходили в обсуждение свежих газетных новостей и биржевых тенденций. Правда, П. Венгерова, стремясь к объективности, отмечала, что среди тех, кто принадлежал к еврейской элите, были и семьи, сохранившие верность традиционным религиозным предписаниям.")]),_._v(" "),v("p",[_._v("Петербургские евреи довели до крайней остроты междоусобные конфликты, всегда имевшие место внутри черты оседлости. Из-за особой политической и культурной роли Петербурга, а также из-за отсутствия в городе национального прошлого с его установлениями еврейские учреждения в столице создавались в условиях жестокой борьбы за абсолютную власть в общине.")]),_._v(" "),v("p",[_._v("После реорганизации правления в 1869 году буквально вся общинная жизнь в столице попала в полную зависимость от добровольных пожертвований нескольких процветающих еврейских семейств. Бароны Гинцбурги, стоявшие во главе общины (сначала Евзель, а затем его сыновья Гораций и Давид), сформировали власть, передававшуюся на самом деле по наследству. Они имели доступ к высшим официальным лицам в государстве и обладали огромной популярностью за пределами Питера – в качестве благотворителей и заступников. Возможность представить себе положение этого уникального семейства позволяет, к примеру, тот факт, что между собой, в обиходной речи, петербургские евреи называли Горация Гинцбурга не иначе как «папаша». Он обладал аристократическими манерами и, подобно многим русским аристократам, чувствовал себя более естественно, изъясняясь по-французски, нежели по-русски. Среди гостей в фешенебельном доме Гинцбурга на Английской набережной можно было встретить знаменитых русских писателей и художников, генералов, юристов, крупных правительственных чиновников. Во время поездок по обширным своим поместьям в Подольской губернии Гинцбурги зачастую были буквально осаждаемы толпами бедных евреев, умолявших о денежной помощи или заступничестве разного рода. В народном сознании, в сознании людей, истосковавшихся по могущественному защитнику, самые разные богатые и влиятельные евреи Петербурга, которым случилось носить фамилии Гинсберг, Гинзбург, Гинцбург или Гюнцбург, слились в одного «барона Гинцбурга»; именно ему приписывали все добрые дела.")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/154/lokshin.files/image005.jpg",alt:""}})]),_._v(" "),v("p",[v("em",[_._v("Барон Г. Е. Гинцбург и адвокат Г. Б. Слиозберг")])]),_._v(" "),v("p",[_._v("Между тем положение в общине продолжало вызывать раздражение у еврейской интеллигенции. Она считала, что именно те самые нувориши, что изображены в «Исповеди» Леванды, ожили и взяли под контроль главную общину еврейства России. В открытом обращении к Гинцбургам и им подобным редакторы «Рассвета» писали в 1880 году:")]),_._v(" "),v("p",[_._v("«Мы, евреи, всё еще не можем стряхнуть с себя печальное вековое наследие, навязанное нам извне… историческим прошлым, всё еще никак не можем освободиться от прискорбного, но основанного, к сожалению, на печальном опыте убеждения, что всего и везде можно достичь лишь деньгами. Деньги, и только одни деньги спасали нас от изгнания, от костров, деньги давали, в некоторых государствах и теперь дают, нам почет и привилегированное положение; почему же, спрашивается, c деньгами, с одними деньгами нельзя было бы и устроить как следует общественные дела? Оказывается, однако, что нельзя, что внутри еврейства необходимы еще и другие рычаги и двигатели… Мы, впрочем, вовсе не против того, чтобы наших финансовых известностей привлекать к участию в общественных делах… Мы только против исключительного участия в этих делах за счет их одних и никого более… Действительный успех могут иметь только общественные дела и начинания, которые будут делом не одиночных личностей, а общим, народным».")]),_._v(" "),v("p",[_._v("Кроме напряженности внутри общины cуществовала и другая проблема. Проблема связей между самой общиной и царскими властями. Последние с особым вниманием и с явным подозрением относились к этому еврейскому учреждению в имперской столице.")]),_._v(" "),v("p",[_._v("Возможно, никогда отношения между общинным правлением и царским правительством не были столь запутанными и оттого характерными для общей ситуации, как при строительстве первой петербургской синагоги. Для многих столичных иудеев дело это по своей важности превосходило все остальные: синагога в политическом и культурном центре империи должна была служить веcомым доказательством незыблемого положения евреев, как таковых, а также воплощением достоинства древнего народа в глазах российского общества, правительства и даже остальных единоверцев.")]),_._v(" "),v("p",[_._v("Еврейские газеты – не только в Петербурге, но и в других городах – постоянно сообщали своим читателям о планах строителей.")]),_._v(" "),v("p",[_._v("А. Ландау писал:")]),_._v(" "),v("p",[_._v("«Еврейский храм в Петербурге – дело весьма важное. Это понимают и осознают все. И всякий еврей, где бы он ни жил – в Петербурге или в Одессе, у подножия Кавказских гор или в холодных снегах Сибири, – всякий внесет свою посильную лепту для этого великого дела… для храма, в котором русскому обществу предстоит познакомиться с самой глубокой стороной еврейского быта – с еврейской религией!»")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/155/lokshin.files/image001.jpg",alt:""}})]),_._v(" "),v("p",[v("em",[_._v("Невский проспект. Конец XIX – начало XX в.")])]),_._v(" "),v("p",[_._v("Слова Ландау свидетельствуют о том, что еврейская элита Петербурга стала смотреть на мир уже чуть ли не с имперских позиций, полагая себя центром, окруженным множеством окраинных земель. Взгляд несколько неожиданный, ибо иные территории, входившие в состав Российской империи, включая Сибирь и Кавказ, фактически были недосягаемы для большинства еврейского населения. Однако общий энтузиазм вообще не знал границ. «Будущая синагога в Петербурге, – утверждал Гордон, – не должна ни в чем уступать синагогам других стран и столиц». Что ж, в конце-то концов, евреев в Российской империи было больше, чем во всех cтранах Европы, вместе взятых…")]),_._v(" "),v("p",[_._v("Идея возведения небывалого молитвенного храма для евреев вдохновила виднейшего русского историка искусства и либерального критика В. В. Стасова. Он начал широкую дискуссию о будущей синагоге. На страницах очередного тома петербургской «Еврейской библиотеки» за 1872 год Стасов утверждал, что это «уже как-то стыдно» – то, что евреи Петербурга «под боком у Европы» не имеют своей синагоги, где смогли бы свободно и открыто молиться. Величественная синагога в столице, считал он, «будет истинно народной честью, славой, потом еще раз докажет, что мы всё более и более поканчиваем с прежними, позорившими нас предрассудками [и что] не хотим уступать остальной Европе в светлости и ширине воззрения». Российская империя, с обилием существовавших в ней этнических групп и вероисповеданий, имела потенциальную возможность стать для Европы образцом религиозной терпимости и поборником гуманистических устремлений. По мнению Стасова, синагога в Петербурге – наряду с уже существовавшими в России православными, католическими, лютеранскими, протестантскими, мусульманскими, армянскими и другими храмами – должна была способствовать вящей славе широкого и благородного русского национального характера.")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/155/lokshin.files/image002.jpg",alt:""}})]),_._v(" "),v("p",[v("em",[_._v("Берлинская синагога")])]),_._v(" "),v("p",[_._v("Как и следовало ожидать, у историка искусства В. В. Стасова были собственные идеи на тот счет, какой надлежит быть синагоге. Дабы подчеркнуть многообразие стилей, дозволенное в империи, она непременно должна была иметь узнаваемый еврейский облик. По существу, значительная часть соображений Стасова была направлена против недавно опубликованного антисемитского опуса Рихарда Вагнера «Das Judentum in der Musik» («Еврейство в музыке»). Стасов отстаивал ту мысль, что евреи обладают собственными эстетическими традициями и отнюдь не склонны к заимствованиям. Подлинно еврейская синагога, говорил он, должна выглядеть «по-восточному», то есть быть построенной в арабско-мавританском стиле синагог средневековой Испании и Германии более позднего периода.")]),_._v(" "),v("p",[_._v("Как бы широко ни приветствовались многими столичными евреями взгляды Стасова, они тем не менее вызвали и серьезные возражения. Известный скульптор еврейского происхождения Марк Антокольский в частном письме Стасову поддержал призыв построить в Петербурге истинно еврейскую синагогу, но усомнился – будет ли мавританский стиль соответствовать этой цели. «Я очень боюсь, – писал он, – чтобы это не было подражание Берлинской синагоге, которая в плане есть подражание протестантской церкви, а протестантская церковь, в свою очередь, есть подражание католической». Следовать этому образцу, с его точки зрения, значило «подражать тому, чему меньше всего должно было бы подражать».")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/155/lokshin.files/image003.jpg",alt:""}})]),_._v(" "),v("p",[v("em",[_._v("Петербургская хоральная синагога")])]),_._v(" "),v("p",[_._v("Крупнейший еврейский писатель и общественный деятель Иеуда-Лейб Гордон высказал почти противоположный взгляд. Он опубликовал ответ Стасову, где отрицал тот факт, что евреи обладают собственным архитектурным стилем. Он утверждал, что на протяжении всей своей истории евреи «заимствовали стиль у господствовавшего в данное время и данной местности народа». Следуя известному своему лозунгу «Будь человеком на улице и евреем дома!», Гордон доказывал, что от века только духовная суть ритуала, Б-гослужения имела значение для евреев, ибо «им совершенно безразлично было, какой внешний вид будет иметь их храм, лишь бы в этой внешности не заключалось ничего шокирующего для их религиозного чувства, ничего тенденциозно-антиеврейского». Другими словами, если евреи когда-то и строили синагоги в арабском стиле, то исключительно потому, что жили среди арабов. Воспроизводить этот стиль в русской столице было бы для евреев так же нелепо, убеждал Гордон Стасова, как говорить в Питере по-арабски, а не по-русски. Синагога в самом сердце России должна была следовать стилю русских православных церквей – но, разумеется, без использования христианских символов. Гордон многое продумал. Не следует водружать на купол синагоги звезду Давида, решил он, ибо, во-первых, еврейское учение вообще отрицает визуальное изображение, а звезда Давида в любом случае есть результат навеянного кабалой «народного заблуждения». По сути, Гордон предлагал удалить все элементы восточного начала в здании еврейского дома молитвы. Евреи действительно молятся, обратясь лицом к востоку (в идеале – к Иерусалиму), говорил писатель, но вовсе не являются истинно восточной нацией.")]),_._v(" "),v("p",[_._v("Смысл его статьи заключался в том, что синагога Петербурга должна продемонстрировать естественную, закономерную совместимость традиций иудаизма с западными нормами. Любопытно, что за вполне поверхностными дискуссиями по проблемам архитектуры у Стасова и Гордона скрывались, оказывается, совершенно противоположные взгляды на роль евреев в Российской империи. Упорное стремление Гордона к полной их ассимиляции полностью подрывало надежды Стасова на то, что славу России принесет ее культурное разнообразие. Стасов призывал евреев сохранить свою особость, непохожесть на других, а Гордон мечтал о полной еврейской интеграции в российское общество.")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/155/lokshin.files/image004.jpg",alt:""}})]),_._v(" "),v("p",[v("em",[_._v("Интерьер синагоги")])]),_._v(" "),v("p",[_._v("Пока велись дебаты об архитектурных стилях, возник еще один спор – о местоположении синагоги. Где найти подходящий кусок земли для большого, заметного здания, претендующего на значительность и оригинальность? Найти среди уже сложившегося городского ландшафта Петербурга с его грандиозными имперскими фасадами, роскошными иностранными магазинами, угрюмыми фабричными строениями и битком набитыми доходными домами? И с его, следует добавить, бесчисленными церквями? Ведь дореволюционный Петербург был городом церквей и соборов, и это сильно усложняло выбор места для синагоги. Императорский указ времен царствования Николая I гласил, что ни одна синагога в стране не может быть построена ближе, чем за сто саженей от здания православного храма на той же самой улице или за пятьдесят саженей на другой. Стасов был всерьез озабочен этим ограничением; он даже советовал городским властям отменить николаевский указ как давно устаревший. Однако его не послушались.")]),_._v(" "),v("p",[_._v("Поиск места начался в 1869 году и продолжался более десяти лет. Казавшиеся бесконечными отсрочки только подогревали раздражение еврейской общины, обвинявшей правление в нехватке инициативы. В действительности же многочисленные предложения правления неизменно принимались наверху; затем, однако, бумаги начинали блуждать по различным (городским и имперским) инстанциям – блуждать, впрочем, лишь для того, чтобы в итоге быть отклоненными. В первом прошении предлагалось назначить для строительства весьма респектабельное место на пересечении набережной Фонтанки и Гороховой улицы – одной из трех радиальных магистралей, вместе с Невским проспектом определяющих планировку центра города. По соседству находились три Подьяческие улицы – самый населенный еврейский район; поблизости стояла временная «купеческая молельня». Но генерал Ф. Ф. Трепов, обер-полицмейстер Петербурга (1866–1873 годы), а затем градоначальник (1873–1878 годы), не поддержал это предложение, объяснив свое решение тем, что по Гороховой улице часто проезжают важные правительственные лица, следуя к Царскосельскому железнодорожному вокзалу, и «нехорошо, чтобы там собирались по субботам и еврейским праздникам массы евреев». Вместо этого Трепов выбрал место для храма на задворках города, в северо-восточной части Выборгского района, где почти совсем не было евреев – так же, правда, как и населения вообще. Правление, в свою очередь, отказалось от обер-полицмейстерской идеи, поскольку это место было расположено в нескольких верстах от Подьяческих улиц, что практически лишало прихожан возможности идти в синагогу пешком по субботам и по праздникам.")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/155/lokshin.files/image005.jpg",alt:""}})]),_._v(" "),v("p",[v("em",[_._v("Интерьер синагоги. Фрагмент паркета")])]),_._v(" "),v("p",[_._v("Правление обратилось к власти с новым ходатайством, отыскав еще одно место поблизости от Подьяческих – на улице Большая Мастерская (ныне Лермонтовский проспект). На этот раз возражения Трепова были более определенными: «Еврейская синагога не должна быть построена в населенной части города во избежание накопления в ней черни и сопряженной с ней грязи». Но если не в населенной части, то где же? Позиция Трепова сулила прежние сложности: сколько придется идти до синагоги пешим ходом? Правление воззвало к высшим властям, к самому царю Александру II. Всё было бесполезно.")]),_._v(" "),v("p",[_._v("Отставка Трепова, последовавшая вскоре за покушением на него ревлюционерки-народницы Веры Засулич (1878 год), оживила волнения и споры вокруг строительства синагоги. Появились новые идеи. И.-Л. Гордон обратился к общественности:")]),_._v(" "),v("p",[_._v("«Русский народ похож в массе своей на добродушное, доверчивое дитя, cлушающее, что старшие говорят. “Образ мыслей” его зависит от того направления, которое ему дают сверху. Поставьте еврейскую синагогу на Невском проспекте – и русский простолюдин, пройдя мимо, снимет шапку и перекрестится: значит, мол, святыня, коль сюда поставили. Но запрячьте ее за Нарвскою заставою – он не только будет смотреть на изгнанную синагогу как на скверну, но сочтет делом, угодным и Б-гу, и начальству, – гнать в шею всех жидов».")]),_._v(" "),v("p",[_._v("Это высказывание примечательно в нескольких отношениях. Оно четко иллюстрирует неистребимую привычку еврейских просветителей взирать на государство как на потенциального союзника евреев и одновременно как на всемогущее божество, повелевающее взглядами и поступками русского человека. Более того, c точки зрения Гордона, мощь высшей власти воплотилась не только в Петербурге, как таковом, – в этом величественном создании Петра, но особенно в Невском проспекте. Конечно, для любого человека Невский проспект имел некое символическое значение, но в понимании Гордона русские воспринимали его как святое место.")]),_._v(" "),v("p",[_._v("Разумеется, о возведении синагоги на Невском проспекте никто и никогда всерьез не заговаривал. Однако отставка Трепова в какой-то мере способствовала скорейшему решению дела. В 1880 году городские и имперские власти утвердили выбор: Большая Мастерская улица… Тем временем еврейская финансовая элита провела подписную кампанию и создала фонд размером более чем 125 тыс. рублей. Самый значительный взнос, 70 тыс. рублей, внес Гораций Гинцбург (при том что общая стоимость проекта составляла 800 тысяч). Правление привлекло специальное жюри для изучения и оценки проектов здания синагоги и, в духе интеграции, включило в его состав нееврея. Им был В. В. Стасов. Репутация Стасова гарантировала ему авторитет гораздо больший, нежели у трех других членов жюри, и тем самым давала возможность провести в жизнь собственные идеи, которые он обнародовал в полемике с Гордоном и Антокольским. Одно предложение Стасов отклонил из-за того, что в представленном конкурсантом проекте здание синагоги явно напоминало церковь. Но что еще важнее, Стасов назвал победителями конкурса Льва Бахмана (первого еврея, закончившего Академию художеств в Петербурге) и И. Шапошникова (русского профессора той же Академии). Их проект для него выделялся «не только арабским стилем», но и тем, что «вообще восточный характер выдержан [там] гораздо лучше и даровитее, чем в остальных проектах; в общем ансамбле здание не имеет никакого сходства ни с… церковью, ни с… мечетью».")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://lechaim.ru/ARHIV/155/lokshin.files/image006.jpg",alt:""}})]),_._v(" "),v("p",[v("em",[_._v("Интерьер синагоги. Менора")])]),_._v(" "),v("p",[_._v("Однако это был еще не конец. Когда правление представило царю проект Бахмана и Шапошникова для окончательного утверждения, Александр II – возможно, это стало его последней волей по отношению к русскому еврейству, ибо он был убит в марте 1881 года – вернул бумагу с лаконичной резолюцией: «Переделать проект в более скромных размерах». Даже Гордон, возражавший против принятого проекта, лишился дара речи, поскольку понял смысл этой фразы: «Чтобы евреи не думали, что они здешние, а не гости только».")]),_._v(" "),v("p",[_._v("Синагога открыла свои двери в 1893 году. Имела место полагающаяся церемония, включавшая в себя обычную в подобных случаях молитву о царской семье. Кантор исполнил «Э-ль мале рахамим» («Г-сподь полон сострадания», молитва об усопших) за упокой Александра II – разумеется, как за «царя-освободителя», а не критика первоначального плана еврейского храма. Гораций Гинцбург, по обыкновению лояльный к власти, заказал для синагоги скульптору М. Антокольскому статую Александра II, но религиозная традиция не позволила поместить ее там. В результате статую отвезли в Академию художеств.")]),_._v(" "),v("p",[_._v("Теперь трудно сообразить, насколько меньше задуманной была построенная синагога. Стоимость постройки оказалась почти вдвое меньше первоначально определенной суммы. Но и с уменьшенным количеством мест (их осталось 1200) синагога всё равно считалась, по тогдашним меркам, достаточно большой. Хотя, конечно, две главные синагоги Одессы (Бродская и Большая), равно как и величественная Ораниенбургская синагога в Берлине, явно крупнее… Более десятка псевдо-минаретов и различных декоративных деталей сообщали зданию желаемый восточный колорит (получилась, по словам Осипа Мандельштама, «пышная чужая смоковница»). Тем не менее служба всегда велась в «западном» стиле, с именными местами для состоятельных жертвователей и хоральным пением. Официально известная как Хоральная синагога (так ее называют и сегодня), она какое-то время неформально звалась «синагогой барона Гинцбурга».")]),_._v(" "),v("p",[_._v("Итак, евреи не вняли призыву раввина Арье-Лейба Эпштейна, не отказались от жизни там, где нельзя точно определить время начала и завершения молитвы. Справедливости ради надо отметить, что впоследствии во всех календарях, издававшихся в столице, это время было указано. Апогей расцвета петербургской еврейской общины, как, пожалуй, и всего еврейства Российской империи, пришелся на канун первой мировой войны. В военные годы, в эпоху революции началась стагнация; за ней наступил кризис.")]),_._v(" "),v("p",[_._v("Но и поныне в Северной столице сохранились давние строения – безмолвные свидетели бурной жизни ушедших столетий. Распахнутые двери Большой синагоги на Лермонтовском проспекте, создающийся в Петербурге Государственный еврейский музей, многочисленные общественно-культурные события в жизни нынешней общины – всё это, должно быть, и есть окно в завтрашний день евреев России.")]),_._v(" "),v("p",[_._v("(Опубликовано в №154, 155, февраль-март 2005)")])])}),[],!1,null,null,null);v.default=t.exports}}]);